package org.tnsif.looping;

import java.util.Scanner;

public class FoeloopExecutor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		for(int j=1;i<=n;j++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("*");
			}
		}
		System.out.println("*");

	}

}
